mapper.select:
SELECT kwd_nm, target, process_num FROM bid_favorite_keywords
process_num IN (SELECT process_num FROM bid_machine_mngs WHERE status = 'Active')
AND bid_status = #{bid_status}
AND process_num = #{process_num}
AND target = #{target}
AND emergency_status == 'True'
AND emergency_status == 'True_final'
AND emergency_status = 'False'
GROUP BY kwd_nm, target

SELECT *
  FROM bid_favorite_keywords


		AND kwd_nm = #{kwd_nm}


		AND target = #{target}


		AND site = #{site}


		AND bid_status = #{bid_status}


  		AND emergency_status = 'False'


  		AND emergency_status != 'False'

SELECT id
FROM search_ads
WHERE name = #{kwd_nm}

SELECT id, created_by, updated_by, process_num, status, cur_crashed_at, cur_rerunned_at, created_at, updated_at
  FROM bid_machine_mngs


		status = #{status}

mapper.update:
UPDATE bid_favorite_keywords
   SET process_num = #{process_num}
 WHERE kwd_nm = #{kwd_nm}
  	   AND target = #{target}
