mapper.select:
SELECT kwd_nm, target, process_num, emergency_status FROM bid_favorite_keywords
process_num IN (SELECT process_num FROM bid_machine_mngs WHERE status = 'Active')
AND bid_status = #{bid_status}
AND process_num = #{process_num}
AND target = #{target}
AND emergency_status = 'True'
AND emergency_status = 'True_final'
AND emergency_status = 'False'
GROUP BY kwd_nm, target

SELECT *
  FROM bid_favorite_keywords


		AND kwd_nm = #{kwd_nm}


		AND target = #{target}


		AND site = #{site}


		AND bid_status = #{bid_status}


  		AND emergency_status = 'False'


  		AND emergency_status != 'False'

SELECT id
FROM search_ads
WHERE name = #{_parameter}

SELECT id, created_by, updated_by, process_num, status, cur_crashed_at, cur_rerunned_at, created_at, updated_at, ip_v4, name, `desc` FROM bid_machine_mngs
status = #{status}
AND process_num = #{process_num}

mapper.update:
UPDATE bid_favorite_keywords
   SET process_num = #{process_num}
 WHERE kwd_nm = #{kwd_nm}
  	   AND target = #{target}

mapper.select:
SELECT bst.kwd_id, ai.adv_id, ai.na_account_ser, bst.color, bst.bid_status, bst.goal_rank, bst.max_bid_amt, bst.goal_cpa, bfk.rank, bfk.emergency_status, bfk.site, bfk.target, bfk.rec_clk_rnk, bfk.rec_clk_at FROM (SELECT kwd_id, color, bid_status, goal_rank, max_bid_amt, goal_cpa FROM bid_schedule_types WHERE bid_status = #{bid_status}) bst, (SELECT kwd_id, bid_favorites_id, SUBSTRING_INDEX(SUBSTRING_INDEX(bid_schedule,',',#{timePosition}),',',-1) color, rank, site, emergency_status, target, rec_clk_rnk, rec_clk_at FROM bid_favorite_keywords WHERE resv_status = 'Active' AND bid_status != 'Inactive'
AND emergency_status = #{emergency_status}
AND kwd_nm = #{kwd_nm} AND target = #{target}
AND bid_schedule IS NOT NULL) bfk, adv_infos ai, bid_favorites bf WHERE bst.kwd_id = bfk.kwd_id AND bst.color = bfk.color AND bf.id = bfk.bid_favorites_id AND bf.advs_id = ai.adv_id

SELECT ai.adv_id, ai.na_account_ser, bfk.* FROM (SELECT * FROM bid_favorite_keywords WHERE bid_status = #{bid_status} AND resv_status = 'Active'
AND kwd_nm = #{kwd_nm} AND target = #{target}
AND emergency_status = #{emergency_status}
AND bid_schedule IS NOT NULL AND SUBSTRING_INDEX(SUBSTRING_INDEX(bid_schedule,',',#{timePosition}),',',-1) = 0) bfk, adv_infos ai, bid_favorites bf WHERE bf.id = bfk.bid_favorites_id AND bf.advs_id = ai.adv_id
